import React, { useRef }  from 'react';
import { useForm } from "react-hook-form";
import { useNavigate } from 'react-router-dom';

const payments = [
  {id: 1, name: 'PayA', info: 'Pay A'},
  {id: 2, name: 'PayB', info: 'Pay B'},
]

class paymentList extends React.Component {
  render () {
    const paymentList = payments.map(e => (
      <li key={e.id}>
        <Link to={'/payment-page/' + e.id}>{e.name}</Link>
      </li>
    ))
    return ;
  }
}

const SelectPayment = () => {
  const { register, handleSubmit } = useForm()
  const navigate = useNavigate();
  const payment = useRef<HTMLInputElement>("");

  const onSubmit = () => {
    // if (payment != "") {
    //   const inputKeyword = payment.current;
    //   navigate(`/payment-page/?keyword=${inputKeyword.value}`);
    //   inputKeyword.value = "";
    // }
  }

  const back = () => {
    navigate(-1);
  };

  return (
    <>
      <label htmlFor='payment'>お店で使える決済サービスを選んでください</label>
      {/* <select
        {...register('payment', {
          required: '* 決済サービスを選んでください'
        })} 
      >
        <option value='' hidden>決済サービスを選択...</option>
        <option value='payA'>Pay A</option>
        <option value='payB'>Pay B</option>
      </select> */}
<paymentList />

      <button type="submit" onSubmit={handleSubmit(onSubmit)}>
        決定
      </button>
      <button type="button" onClick={back}>
        戻る
      </button>
    </>
  );
};

export default SelectPayment;
