import React from 'react';
import { useQRCode } from 'react-hook-qrcode';

const PaymentPage = () => {
    const [inputRef] = useQRCode({
        text: 'https://google.com',
        options: {
            level: 'S',
            margin: 7,
            scale: 1,
            width: 400,
        },
        });
        return (    
        <div>
            <canvas ref={inputRef} />
        </div>
    );
};

export default PaymentPage;
