import React from 'react';
import {  useForm, handleSubmit } from "react-hook-form";
import { useNavigate } from 'react-router-dom';

const SelectPayment = () => {
  const onSubmit = data => console.log(data)

  const navigate = useNavigate();

  const move = () => {
    navigate('/payment-page');
  };
  const back = () => {
    navigate(-1);
  };

  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)} >
        {/* <p>どの決済サービスを使いますか？</p>
        <p></p>
        <label>
        <select {...register("category")}>
          <option value="">Select...</option>
          <option value="A">Pay A</option>
          <option value="B">Pay B</option>
        </select>
        </label> */}

        <label htmlFor='payment'>お店で使える決済サービスを選んでください</label>
          {!!formState.errors.department && 
            <p>{formState.errors.department.message}</p>
          }
        <select
          id='payment'
          isError={!!formState.errors.department}
          {...register('payment', {
            required: '* 決済サービスを選んでください'
          })} 
        >
          <option value='' hidden>決済サービスを選択...</option>
          <option value='payA'>Pay A</option>
          <option value='payB'>Pay B</option>
        </select>

        {/* <button type="submit" disabled={!formState.isDirty || formState.isSubmitting}> */}
        <button type="submit">
          決定
        </button>
        <button type="button" onClick={back}>
          戻る
        </button>
      </form>
    </>
  );
};

export default SelectPayment;
