import React from 'react';
import { useNavigate } from 'react-router-dom';

const SelectPayment = () => {
  const [val, setVal] = React.useState('Radio 1');

  const handleChange = e => setVal(e.target.value);
  const navigate = useNavigate();

  const move = () => {
    navigate('/Paing');
  };

  return (
    <>
      <p>どの決済サービスを使いますか？</p>
      <p>お店で使える決済サービスを選んでください</p>
      <label>
        <input
          type="radio"
          name='radioGroup'
          id='radioGroup1'
          value="pay 1"
          onChange={handleChange}
          checked={val === 'pay 1'}
        />
 　　　　pay 1
      </label>
      <label>
        <input
          type="radio"
          name='radioGroup'
          value="pay 2"
          onChange={handleChange}
          checked={val === 'pay 2'}
        />
        pay 2
      </label>
      <button type="button" onClick={move}>
        決定
      </button>
    </>
  );
};

export default SelectPayment;
